if("undefined"!=typeof slide_data_logo){var logodata=slide_data_logo,data_logo_parsed_to_array=logodata.map(e=>Object.values(e)),holder=document.getElementById("holder");function setLogoSlides(){document.querySelectorAll(".logoslides .slide").forEach(e=>{var o=e.getAttribute("id");document.getElementById(o).addEventListener("click",e=>{e.preventDefault(),slideModal(o,"logo")})})}function logoSlideshowDisplayControl(){var e=window.innerWidth,o=holder.className;e>=medium_width?holder.setAttribute("class",o.replace("hidden","")):e<medium_width&&-1===o.indexOf("hidden")&&holder.setAttribute("class",o+" hidden")}window.addEventListener("load",function(){logoSlideshowDisplayControl(holder)}),window.addEventListener("resize",function(){logoSlideshowDisplayControl(holder)})}